class ASTScope {
  vars = (() => new Map())();
  constructor(parent) {
    this.parent = parent;
  }
  define(name, kind, namespace) {
    this.vars.set(name, {
      kind,
      namespace
    });
  }
  lookup(name) {
    if (this.vars.has(name)) return this.vars.get(name);
    return this.parent?.lookup(name);
  }
}

export { ASTScope as default };
